type Trie struct {
    children []*Trie
    isWord bool // 
    part byte
}


/** Initialize your data structure here. */
func Constructor() Trie {
    return Trie{}
}


/** Inserts a word into the trie. */
func (this *Trie) Insert(word string)  {
    this.insert(word, 0)
}

func (this *Trie) insert(word string, height int) {
    if len(word) == height {
        this.isWord = true
        return
    }
    // 
    w := word[height]
    child := this.matchChild(w)
    // 
    if child == nil {
        child = &Trie{part: w}
        this.children = append(this.children, child)
    }
    // 
    child.insert(word, height + 1)
}

func (this *Trie) matchChild(w byte) *Trie {
    for _, child := range this.children {
        if child.part == w {
            return child
        }
    }
    return nil
}


/** Returns if the word is in the trie. */
func (this *Trie) Search(word string) bool {
    return this.search(word, 0)
}

func (this *Trie) search(word string, height int) bool {
    if len(word) == height {
        if this.isWord {
            return true
        }
        return false
    }
    for _, child := range this.children {
        if child.part == word[height] {
            return child.search(word, height + 1)
        }
    }
    return false
}


